def check_password_against_common_password_list(password, list_file_path):
    hashed_password = argon2.PasswordHasher(salt=b'\x00' * 16).hash(password)
    with open(list_file_path, 'r') as list_file:
        for line in list_file:
            if hashed_password == line.strip():
                return True
    return False

